package abstract_multiple;

public class Abstract3 extends BaseClass{

	float a,b;
	
	@Override
	public float sum() {
		// TODO Auto-generated method stub
		return a+b;
	}

	@Override
	float sub() {
		// TODO Auto-generated method stub
		return a-b;
	}

	@Override
	public float mul() {
		// TODO Auto-generated method stub
		return a*b;
	}

}
